        $('#service_nai').click(function(){
            $('body, html').animate({
                "scrollTop": $("#nai").offset().top - 50
            }, 800, "swing"); // animācijas laiks un beigu kustība
            return false; // prevent default
        });
        $('#service_lab').click(function(){
            $('body, html').animate({
                "scrollTop": $("#lab").offset().top - 50
            }, 800, "swing"); // animācijas laiks un beigu kustība
